import {assert} from '@ciscospark/test-helper-chai';
import sinon from '@ciscospark/test-helper-sinon';
import MockSpark from '@ciscospark/test-helper-mock-spark';
import Lyra, {config as lyraConfig} from '../..';

describe(`plugin-lyra`, () => {
  const lyraSpaceId = `lyra-1337`;
  const lyraSpaceUrl = `https://lyra/api/v1/${lyraSpaceId}`;
  const lyraSpace = {
    identity: {
      id: lyraSpaceId
    },
    url: lyraSpaceUrl
  };
  const conversation = {
    url: `https://conversation`,
    kmsResourceObjectUrl: `https://kms`
  };

  let spark;

  before(() => {
    spark = new MockSpark({
      children: {
        lyra: Lyra
      },
      device: {
        url: `deviceUrl`,
        userId: `1234`
      }
    });
    spark.config.lyra = lyraConfig.lyra;
  });

  beforeEach(() => {
    spark.request.reset();
  });

  describe(`spaces`, () => {
    describe(`#get()`, () => {
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.get(), /space.id is required/));
    });

    describe(`#joinSpace()`, () => {
      it(`defaults to MANUAL pass type`, () => spark.lyra.spaces.joinSpace(lyraSpace)
          .then(() => assert.calledWith(spark.request, sinon.match({
            method: `PUT`,
            uri: `${lyraSpace.url}/occupants/@me`,
            body: {
              pass: {
                type: `MANUAL`
              },
              deviceUrl: `deviceUrl`
            }
          }))));

      it(`allows other pass type`, () => spark.lyra.spaces.joinSpace(lyraSpace, {passType: `TEST`})
          .then(() => assert.calledWith(spark.request, sinon.match({
            method: `PUT`,
            uri: `${lyraSpace.url}/occupants/@me`,
            body: {
              pass: {
                type: `TEST`
              },
              deviceUrl: `deviceUrl`
            }
          }))));
    });

    describe(`#bindConversationToSpace`, () => {
      it(`requires space.url`, () => assert.isRejected(spark.lyra.spaces.bindConversationToSpace(), /space.url is required/));
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.bindConversationToSpace({url: lyraSpaceUrl}), /space.id is required/));
      it(`requires conversation.url`, () => assert.isRejected(spark.lyra.spaces.bindConversationToSpace(lyraSpace, {kmsResourceObjectUrl: `url`}), /conversation.url is required/));
      it(`requires conversation.kmsResourceObjectUrl`, () => assert.isRejected(spark.lyra.spaces.bindConversationToSpace(lyraSpace, {url: `url`}), /conversation.kmsResourceObjectUrl is required/));
    });

    describe(`#unbindConversationFromSpace`, () => {
      it(`requires space.url`, () => assert.isRejected(spark.lyra.spaces.unbindConversationFromSpace(), /space.url is required/));
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.unbindConversationFromSpace({url: lyraSpaceUrl}), /space.id is required/));
      it(`requires conversation.url`, () => assert.isRejected(spark.lyra.spaces.unbindConversationFromSpace(lyraSpace, {kmsResourceObjectUrl: `url`}), /conversation.url is required/));
      it(`requires conversation.kmsResourceObjectUrl`, () => assert.isRejected(spark.lyra.spaces.unbindConversationFromSpace(lyraSpace, {url: conversation.url}), /conversation.kmsResourceObjectUrl is required/));
    });

    describe(`#deleteBinding`, () => {
      it(`requires space.url`, () => assert.isRejected(spark.lyra.spaces.deleteBinding(), /space.url is required/));
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.deleteBinding({url: lyraSpaceUrl}), /space.id is required/));
      it(`requires options.kmsResourceObjectUrl`, () => assert.isRejected(spark.lyra.spaces.deleteBinding(lyraSpace, {bindingId: `123`}), /kmsResourceObjectUrl is required/));
      it(`requires options.bindingId`, () => assert.isRejected(spark.lyra.spaces.deleteBinding(lyraSpace, {kmsResourceObjectUrl: `url`}), /options.bindingId is required/));
    });

    describe(`#putAudioState`, () => {
      it(`requires audioState.deviceUrl`, () => assert.isRejected(spark.lyra.spaces.putAudioState(lyraSpace), /audioState.deviceUrl is required/));
    });

    describe(`#setVolume`, () => {
      it(`defaults to level 0`, () => spark.lyra.spaces.setVolume(lyraSpace)
          .then(() => {
            assert.calledWith(spark.request, sinon.match({
              method: `POST`,
              uri: `${lyraSpace.url}/audio/volume/actions/set/invoke`,
              body: {
                level: 0
              }
            }));
          }));
    });
  });

});
