import {assert} from '@ciscospark/test-helper-chai';
import MockSpark from '@ciscospark/test-helper-mock-spark';
import Lyra, {config as lyraConfig} from '../..';

describe(`plugin-lyra`, () => {
  const lyraSpaceId = `lyra-1337`;
  const lyraSpaceUrl = `https://lyra/api/v1/${lyraSpaceId}`;
  const lyraSpace = {
    identity: {
      id: lyraSpaceId
    },
    url: lyraSpaceUrl
  };
  const conversation = {
    url: `https://conversation`,
    kmsResourceObjectUrl: `https://kms`
  };

  let spark;

  before(() => {
    spark = new MockSpark({
      children: {
        lyra: Lyra
      },
      device: {
        url: `deviceUrl`,
        userId: `1234`
      }
    });

    spark.config.lyra = lyraConfig.lyra;
  });

  beforeEach(() => {
    spark.request.reset();
  });

  describe(`spaces`, () => {
    describe(`#get()`, () => {
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.get(), /space.id is required/));
    });

    describe(`#bindConversation()`, () => {
      it(`requires space.url`, () => assert.isRejected(spark.lyra.spaces.bindConversation(), /space.url is required/));
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.bindConversation({url: lyraSpaceUrl}), /space.id is required/));
      it(`requires conversation.url`, () => assert.isRejected(spark.lyra.spaces.bindConversation(lyraSpace, {kmsResourceObjectUrl: `url`}), /conversation.url is required/));
      it(`requires conversation.kmsResourceObjectUrl`, () => assert.isRejected(spark.lyra.spaces.bindConversation(lyraSpace, {url: `url`}), /conversation.kmsResourceObjectUrl is required/));
    });

    describe(`#unbindConversation()`, () => {
      it(`requires space.url`, () => assert.isRejected(spark.lyra.spaces.unbindConversation(), /space.url is required/));
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.unbindConversation({url: lyraSpaceUrl}), /space.id is required/));
      it(`requires conversation.url`, () => assert.isRejected(spark.lyra.spaces.unbindConversation(lyraSpace, {kmsResourceObjectUrl: `url`}), /conversation.url is required/));
      it(`requires conversation.kmsResourceObjectUrl`, () => assert.isRejected(spark.lyra.spaces.unbindConversation(lyraSpace, {url: conversation.url}), /conversation.kmsResourceObjectUrl is required/));
    });

    describe(`#deleteBinding`, () => {
      it(`requires space.url`, () => assert.isRejected(spark.lyra.spaces.deleteBinding(), /space.url is required/));
      it(`requires space.id`, () => assert.isRejected(spark.lyra.spaces.deleteBinding({url: lyraSpaceUrl}), /space.id is required/));
      it(`requires options.kmsResourceObjectUrl`, () => assert.isRejected(spark.lyra.spaces.deleteBinding(lyraSpace, {bindingId: `123`}), /kmsResourceObjectUrl is required/));
      it(`requires options.bindingId`, () => assert.isRejected(spark.lyra.spaces.deleteBinding(lyraSpace, {kmsResourceObjectUrl: `url`}), /options.bindingId is required/));
    });
  });

});
