import LoggerProxy from '../logs/logger-proxy';

import Events from './events';

const EventsProxy = {};

EventsProxy.emit = (eventName, ...args) => {
  LoggerProxy.logger.log(`EventsProxy->emit#${eventName} -- ${args}`);
  return Events.emit(eventName, args);
};

EventsProxy.on = Events.on;

EventsProxy.get = () => Events;

export default EventsProxy;
