import LoggerConfig from '../logs/logger-config';
import LoggerProxy from '../logs/logger-proxy';

import Events from './events';

const EventsProxy = {};

EventsProxy.emit = (eventName, ...args) => {
  let argString = '';

  try {
    argString = JSON.stringify(args);
  }
  catch (e) {
    // no op
  }
  LoggerProxy.logger.trace(`EventsProxy->emit#${eventName}${LoggerConfig.verboseEvents ? ` -- ${argString}` : ''}`);

  return Events.emit(eventName, args);
};

EventsProxy.on = Events.on;

EventsProxy.get = () => Events;

export default EventsProxy;
