import EventEmitter from 'events';

import LoggerProxy from '../logs/logger-proxy';
import LoggerConfig from '../logs/logger-config';

/**
 * Events
 * plugin-meetings global
 * Used to emit events internally between modules
 */
class Events extends EventEmitter {
  emit(eventName, args) {
    let argString = '';

    try {
      argString = JSON.stringify(args);
    }
    catch (e) {
      // no op
    }
    LoggerProxy.logger.warn(`EventsProxy->emit#${eventName}${LoggerConfig.verboseEvents ? ` -- ${argString}` : ''}`);

    return super.emit(eventName, args);
  }
}

export default new Events();
