import {} from '../constants';

const hostUtils = {};

hostUtils.parse = (host) => {
  if (host) {
    return {
      id: hostUtils.getId(host)
    };
  }
  return null;
};

hostUtils.getHosts = (oldHost, newHost) => {
  const previous = oldHost;
  const current = newHost && hostUtils.parse(newHost);
  return {
    previous,
    current,
    updates: {
      isNewHost: previous && current ? hostUtils.isDifferentHosts(previous.id, current.id) : true
    }
  };
};

hostUtils.isDifferentHosts = (previousId, currentId) => previousId !== currentId;

hostUtils.getId = (host) => {
  if (!host) {
    return null;
  }
  return host.id;
};

export default hostUtils;
