/* globals window, MediaStream */
import bowser from 'bowser';

import {
  RTC_CONFIGURATION,
  RTC_CONFIGURATION_FIREFOX
} from '../constants';

const MediaUtil = {};

MediaUtil.createPeerConnection = () => new window.RTCPeerConnection(
  bowser.firefox ? RTC_CONFIGURATION_FIREFOX : RTC_CONFIGURATION
);

MediaUtil.createMediaStream = (tracks) => {
  if (!tracks) {
    console.error('Tracks doesnt exist');
  }

  return new MediaStream(tracks);
};

export default MediaUtil;
