import {assert} from '@ciscospark/test-helper-chai';

describe(`ciscospark`, () => {
  describe(`commonjs`, () => {
    it(`loads correctly when required in the commonjs style`, () => {
      // eslint-disable-next-line global-require
      const CiscoSpark = require(`ciscospark`);
      const spark = new CiscoSpark();
      assert.hasProperty(spark, `canAuthorize`);
      assert.hasProperty(spark, `credentials`);
      assert.hasProperty(spark, `memberships`);
      assert.hasProperty(spark, `messages`);
      assert.hasProperty(spark, `people`);
      assert.hasProperty(spark, `phone`);
      assert.hasProperty(spark, `rooms`);
      assert.hasProperty(spark, `teamMemberships`);
      assert.hasProperty(spark, `teams`);
      assert.hasProperty(spark, `webhooks`);
    });
  });
});
